    TrUserData = proplists:get_value(user_data, Opts),
    case MsgName of
      access_by_key_req ->
	  v_msg_access_by_key_req(Msg, [MsgName], TrUserData);
      thing_id -> v_msg_thing_id(Msg, [MsgName], TrUserData);
      access_by_id_req ->
	  v_msg_access_by_id_req(Msg, [MsgName], TrUserData);
      token -> v_msg_token(Msg, [MsgName], TrUserData);
      user_id -> v_msg_user_id(Msg, [MsgName], TrUserData);
      empty -> v_msg_empty(Msg, [MsgName], TrUserData);
      _ -> mk_type_error(not_a_known_message, Msg, [])
    end.


-compile({nowarn_unused_function,v_msg_access_by_key_req/3}).
-dialyzer({nowarn_function,v_msg_access_by_key_req/3}).
v_msg_access_by_key_req(#{} = M, Path, TrUserData) ->
    case M of
      #{token := F1} ->
	  v_type_string(F1, [token | Path], TrUserData);
      _ -> ok
    end,
    case M of
      #{chanID := F2} ->
	  v_type_string(F2, [chanID | Path], TrUserData);
      _ -> ok
    end,
    lists:foreach(fun (token) -> ok;
		      (chanID) -> ok;
		      (OtherKey) ->
			  mk_type_error({extraneous_key, OtherKey}, M, Path)
		  end,
		  maps:keys(M)),
    ok;
v_msg_access_by_key_req(M, Path, _TrUserData)
    when is_map(M) ->
    mk_type_error({missing_fields, [] -- maps:keys(M),
		   access_by_key_req},
		  M, Path);
